import axios from 'axios';
import NProgress from 'nprogress';
import { Notification } from 'element-ui';
import envConfig from '../global/environment';
import { createRouter } from '../../../router/index';

const router = createRouter();

let xhrCacheList = [];
const CancelToken = axios.CancelToken;
const prefix = '/api';
const isServer = typeof global !== 'undefined';

// 请求发送拦截
axios.interceptors.request.use(function (config) {
  return config;
}, function (error) {
  return Promise.reject(error);
});

// 增加响应拦截器（判断用户是否登录）
axios.interceptors.response.use((response) => {
  return response;
}, function (error) {
  return Promise.reject(error);
});

export default function Http (config) {
  const obj = {};
  const commonHeaders = {
    'Content-Type': 'application/json'
  };
  if (isServer && global.__VUE_SSR_CONTEXT__) {
    commonHeaders.Cookie = global.__VUE_SSR_CONTEXT__.cookies;
  }
  const axiosConfig = {
    method: config.method || 'get',
    url: envConfig.SERVER_ADDRESS + prefix + config.url,
    withCredentials: config.withCredentials || true,
    headers: Object.assign({}, commonHeaders, config.headers),
    cancelToken: new CancelToken(function executor (cancel) {
      obj.cancel = function () {
        // 如果是取消的请求，则在失败时也不现实错误提示
        obj.config.showErrorMsg = false;

        cancel();
      };
    })
  };

  obj.config = config;

  // 设置请求参数
  axiosConfig.method === 'get'
    ? axiosConfig.params = config.data
    : axiosConfig.data = config.data;

  obj.xhrInstance = new Promise((resolve, reject) => {

    if (!isServer) {
      // 客户端渲染 开启滚动条
      NProgress.start();
    }

    axios(axiosConfig).then(response => {
      const data = response.data;

      // 登录验证
      loginCheck(data);

      if (data.code !== 0) {
        if (config.showErrorMsg) {
          Notification.error(data.message);
        }
        reject(data.message);
      } else {
        if (config.showSuccessMsg) {
          // 如果前端设置了提示信息则采用前端设置的提示信息，否则使用服务端返回的提示信息
          Notification.success(typeof config.showSuccessMsg === 'string' ? config.showSuccessMsg : data.message);
        }
        resolve(data.data);
      }
    }).catch(function (error) {

      if (config.showErrorMsg) {
        Notification.error(typeof config.showErrorMsg === 'string' ? config.showErrorMsg : error.message);
      }
      reject(error);
    }).finally(() => {

      // 关闭loading
      if (config.loading) {
        obj.loadingInstance.close();
      }

      // 更新请求缓存列表
      const xhrIndex = xhrCacheList.indexOf(obj);
      xhrCacheList.splice(xhrIndex, 1);

      if (xhrCacheList.length === 0) {
        if (!isServer) {
          NProgress.done();
        }
      }
    });
  });

  if (config.loading) {
    obj.loadingInstance = this.$loading({
      target: config.loading,
      spinner: 'app-loading'
    });
  }

  xhrCacheList.push(obj);

  return obj;
}

Http.cancelAll = function () {
  xhrCacheList.forEach(item => {
    item.cancel();
  });

  // 清空请求缓存列表
  xhrCacheList = [];
};

Http.getXhrCacheList = function () {
  return xhrCacheList;
};

// 用户未登录处理
function loginCheck (response) {
  // 未登录
  if (response.code === -1000) {
    router.push('/login');
  }
}

